PREFIX agrontology: <http://aims.fao.org/aos/agrontology#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

SELECT ?soilLabel ?predicate ?objectLabel
WHERE {
  ?soil skos:prefLabel "soils"@en ;
        skos:prefLabel ?soilLabel . 

  {
    ?soil agrontology:isComposedOf ?object .
    BIND(agrontology:isComposedOf AS ?predicate)
    ?object skos:prefLabel ?objectLabel .
  }
  UNION
  {
    ?soilFunctions skos:prefLabel "soil functions"@en ;
                   skos:prefLabel ?objectLabel . 
    ?soil ?predicate ?soilFunctions .
  }
}

ORDER BY ?predicate ?objectLabel

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX obo: <http://purl.obolibrary.org/obo/>

SELECT DISTINCT ?itemLabel ?predicate ?relatedItemLabel
WHERE {
  VALUES ?practiceLabelString { "sustainable soil management"@en "soil protection"@en }
  ?practice skos:prefLabel ?practiceLabelString ;
            skos:prefLabel ?itemLabel .

  ?practice ?predicate ?targetOfPractice .
  FILTER(?predicate = obo:T4FS_0000577)

  ?targetOfPractice skos:prefLabel ?relatedItemLabel .
}

ORDER BY ?itemLabel ?predicate ?relatedItemLabel

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

SELECT DISTINCT ?concept ?uniqueDefinition
WHERE {
  ?concept skos:prefLabel ?label ;
           skos:definition ?uniqueDefinition .
  FILTER(?label = "soil health"@en || ?label = "soil quality"@en)

  FILTER NOT EXISTS {
    ?otherConcept skos:prefLabel ?otherLabel ;
                  skos:definition ?uniqueDefinition .
    FILTER(?otherLabel != ?label && 
           (?otherLabel = "soil health"@en || ?otherLabel = "soil quality"@en))
  }
}

PREFIX she: <https://soilwise-he.github.io/soil-health#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

SELECT ?resultType ?value
WHERE {
  ?threat skos:prefLabel "soil threats"@en .

  {
    ?threat skos:definition ?value .
    BIND("Definition" AS ?resultType)
  }
  UNION
  {
    ?threat she:isIndicatedBy ?indicator .
    ?indicator skos:prefLabel ?value .
    BIND("Indicator" AS ?resultType)
  }
}
ORDER BY ?resultType

PREFIX she: <https://soilwise-he.github.io/soil-health#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

SELECT ?resultType ?value
WHERE {
  ?degradation skos:prefLabel "soil degradation"@en .

  {
    ?degradation skos:definition ?value .
    BIND("Definition" AS ?resultType)
  }
  UNION
  {
    ?degradation she:isIndicatedBy ?indicator .
    ?indicator skos:prefLabel ?value .
    BIND("Indicator" AS ?resultType)
  }
}

ORDER BY ?resultType

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX sorelph: <http://sweetontology.net/relaPhysical/>

SELECT ?resultType ?value
WHERE {
  ?thresholdsConcept skos:prefLabel "thresholds"@en .
  {
    ?thresholdsConcept skos:definition ?value .
    BIND("Definition" AS ?resultType)
  }
  UNION
  {
    ?soilProtectionConcept skos:prefLabel "soil protection"@en .
    ?soilProtectionConcept obo:T4FS_0000577 ?criticalLevelConcept .
    ?criticalLevelConcept sorelph:from ?thresholdsConcept .
    ?criticalLevelConcept skos:prefLabel ?value .

    BIND("Information Provided by Thresholds (for Soil Protection)" AS ?resultType)
  }
}

ORDER BY ?resultType DESC(?value)

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX agrontology: <http://aims.fao.org/aos/agrontology#>

SELECT ?resultType ?value ?valueLabel
WHERE {
  ?ecosystemServicesConcept skos:prefLabel "ecosystem services"@en .

  {
    ?ecosystemServicesConcept skos:definition ?definition .
    BIND("Definition" AS ?resultType)
    BIND(?definition AS ?value)
  }
  UNION
  {
    ?ecosystemServicesConcept agrontology:includes ?typeOfService .
    ?typeOfService skos:prefLabel ?typeLabel .
    BIND("Type of Ecosystem Service" AS ?resultType)
    BIND(?typeOfService AS ?value)
    BIND(?typeLabel AS ?valueLabel)
  }
}
ORDER BY ?resultType ?valueLabel

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX prov: <http://www.w3.org/ns/prov#>

SELECT ?modelLabel ?countryLabel
WHERE {
  ?riskAssessmentModelsCategory skos:prefLabel "risk assessment models"@en .
  ?riskAssessmentModelsCategory skos:narrower ?model .
  ?model skos:prefLabel ?modelLabel .

  OPTIONAL {
    ?model prov:atLocation ?countryURI .
    ?countryURI skos:prefLabel ?countryLabel .
  }
}
ORDER BY ?modelLabel ?countryLabel

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX she: <https://soilwise-he.github.io/soil-health#>
PREFIX agrontology: <http://aims.fao.org/aos/agrontology#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT DISTINCT ?subject ?predicate ?object ?contextLabel
WHERE {
  ?sbdConcept skos:prefLabel "soil biological degradation"@en .

  {
    ?sbdConcept skos:definition ?definitionText .
    BIND(?sbdConcept AS ?subject)
    BIND(skos:definition AS ?predicate)
    BIND(?definitionText AS ?object)
  }
  UNION
  {
    ?sbdConcept she:isAccompaniedBy ?accompaniedByConcept .
    ?accompaniedByConcept skos:prefLabel ?accompaniedByLabel .
    BIND(?sbdConcept AS ?subject)
    BIND(she:isAccompaniedBy AS ?predicate)
    BIND(?accompaniedByLabel AS ?object)
  }
  UNION
  {
    ?sbdConcept agrontology:causes ?bNodeCaused .
    ?bNodeCaused rdf:object ?causedObject .
    ?causedObject skos:prefLabel ?causedObjectLabel .
    BIND(?sbdConcept AS ?subject)
    BIND(agrontology:causes AS ?predicate)
    BIND(?causedObjectLabel AS ?object)
    OPTIONAL {
        ?bNodeCaused obo:BFO_0000066 ?context .
        ?context skos:prefLabel ?ctxLabel .
        BIND(?ctxLabel AS ?contextLabel)
    }
    BIND(IF(BOUND(?contextLabel), ?contextLabel, "") AS ?contextLabel)
  }
  UNION
  {
    ?sbdConcept obo:RO_0002212 ?relatedObjectOrBNode .
    OPTIONAL {
        ?relatedObjectOrBNode rdf:object ?actualObject .
        ?actualObject skos:prefLabel ?actualObjectLabel .
        OPTIONAL {
            ?relatedObjectOrBNode obo:BFO_0000066 ?context .
            ?context skos:prefLabel ?ctxLabel .
        }
    }
    OPTIONAL {
      FILTER(!ISBLANK(?relatedObjectOrBNode))
      ?relatedObjectOrBNode skos:prefLabel ?directObjectLabel .
    }
    BIND(?sbdConcept AS ?subject)
    BIND(obo:RO_0002212 AS ?predicate)
    BIND(COALESCE(?actualObjectLabel, ?directObjectLabel) AS ?object)
    BIND(IF(BOUND(?ctxLabel), ?ctxLabel, "") AS ?contextLabel)
  }
  UNION
  {
    ?sbdConcept obo:RO_0002213 ?relatedObjectOrBNode_2213 .
    OPTIONAL {
        ?relatedObjectOrBNode_2213 rdf:object ?actualObject_2213 .
        ?actualObject_2213 skos:prefLabel ?actualObjectLabel_2213 .
        OPTIONAL {
            ?relatedObjectOrBNode_2213 obo:BFO_0000066 ?context_2213 .
            ?context_2213 skos:prefLabel ?ctxLabel_2213 .
        }
    }
    OPTIONAL {
      FILTER(!ISBLANK(?relatedObjectOrBNode_2213))
      ?relatedObjectOrBNode_2213 skos:prefLabel ?directObjectLabel_2213 .
    }
    BIND(?sbdConcept AS ?subject)
    BIND(obo:RO_0002213 AS ?predicate)
    BIND(COALESCE(?actualObjectLabel_2213, ?directObjectLabel_2213) AS ?object)
    BIND(IF(BOUND(?ctxLabel_2213), ?ctxLabel_2213, "") AS ?contextLabel)
  }
}
ORDER BY ?subject ?predicate

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX biolink: <https://w3id.org/biolink/vocab/>
PREFIX prov: <http://www.w3.org/ns/prov#>
PREFIX af-x: <http://purl.allotrope.org/ontologies/property#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX om: <http://www.ontology-of-units-of-measure.org/resource/om-2/>
PREFIX sorelm: <http://sweetontology.net/relaMath/>

SELECT ?socPercentage ?countryLabel ?factorOrLandUseLabel
WHERE {
  ?siteTypicalSocConcept skos:prefLabel "site-typical SOC contents"@en .

  {
    ?siteTypicalSocConcept om:hasValue ?valueNode .
  }
  UNION
  {
    ?siteTypicalSocConcept sorelm:hasUpperBound ?valueNode .
  }

  ?valueNode biolink:has_percentage ?socPercentage .
  ?valueNode prov:atLocation ?locationURI .
  ?locationURI skos:prefLabel ?countryLabel .

  OPTIONAL {
    ?valueNode af-x:AFX_0000408 ?environmentalFactor .
    ?environmentalFactor skos:prefLabel ?factorLabel .
    BIND(?factorLabel AS ?factorOrLandUseLabel)
  }
  OPTIONAL {
    ?valueNode obo:BFO_0000066 ?landUseContext .
    ?landUseContext skos:prefLabel ?landUseLabel .
    BIND(?landUseLabel AS ?factorOrLandUseLabel)
  }
}
ORDER BY ?countryLabel ?socPercentage ?factorOrLandUseLabel

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX she: <https://soilwise-he.github.io/soil-health#>
PREFIX sorelm: <http://sweetontology.net/relaMath/>
PREFIX af-x: <http://purl.allotrope.org/ontologies/property#>
PREFIX prov: <http://www.w3.org/ns/prov#>
PREFIX qudt: <http://qudt.org/schema/qudt/>

SELECT ?landClassificationLabel ?predicateURI ?value
WHERE {
  ?socClayRatioConcept skos:prefLabel "SOC/clay ratio"@en .

  {
    ?socClayRatioConcept sorelm:hasUpperBound ?upperBoundNode .
    ?upperBoundNode af-x:AFX_0000408 she:DegradedLand ;
                    prov:atLocation she:Wallonia ;
                    qudt:numericValue ?numericValueUB .
    she:DegradedLand skos:prefLabel ?landClassificationLabel .
    BIND(sorelm:hasUpperBound AS ?predicateURI)
    BIND(STR(?numericValueUB) AS ?value)
  }
  UNION
  {
    ?socClayRatioConcept af-x:AFX_0002696 ?intervalNode .
    ?intervalNode af-x:AFX_0000408 she:TransitionalLand ;
                  prov:atLocation she:Wallonia ;
                  sorelm:hasInterval ?intervalValue .
    she:TransitionalLand skos:prefLabel ?landClassificationLabel .
    BIND(af-x:AFX_0002696 AS ?predicateURI)
    BIND(?intervalValue AS ?value)
  }
  UNION
  {
    ?socClayRatioConcept sorelm:hasLowerBound ?lowerBoundNodeF .
    ?lowerBoundNodeF af-x:AFX_0000408 she:FavourableLand ;
                     prov:atLocation she:Wallonia ;
                     qudt:numericValue ?numericValueLBF .
    she:FavourableLand skos:prefLabel ?landClassificationLabel .
    BIND(sorelm:hasLowerBound AS ?predicateURI)
    BIND(STR(?numericValueLBF) AS ?value)
  }
  UNION
  {
    ?socClayRatioConcept sorelm:hasLowerBound ?lowerBoundNodeHF .
    ?lowerBoundNodeHF af-x:AFX_0000408 she:HighlyFavourableLand ;
                      prov:atLocation she:Wallonia ;
                      qudt:numericValue ?numericValueLBHF .
    she:HighlyFavourableLand skos:prefLabel ?landClassificationLabel .
    BIND(sorelm:hasLowerBound AS ?predicateURI)
    BIND(STR(?numericValueLBHF) AS ?value)
  }
}
ORDER BY ?landClassificationLabel ?predicateURI

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX obo: <http://purl.obolibrary.org/obo/>

SELECT ?item ?propertyOrRole ?valueOrFactorLabel
WHERE {
  {
    ?csConcept skos:prefLabel "carbon sequestration"@en .
    ?csConcept skos:definition ?definition .
    BIND("Carbon Sequestration" AS ?item)
    BIND(skos:definition AS ?propertyOrRole)
    BIND(?definition AS ?valueOrFactorLabel)
  }
  UNION
  {
    ?sspConcept skos:prefLabel "soil sequestration potential"@en .
    {
      ?sspConcept obo:RO_0002336 ?influencingFactor .
      ?influencingFactor skos:prefLabel ?influencingFactorLabel .
      BIND("Soil Sequestration Potential" AS ?item)
      BIND(obo:RO_0002336 AS ?propertyOrRole)
      BIND(?influencingFactorLabel AS ?valueOrFactorLabel)
    }
    UNION
    {
      ?outputConcept obo:RO_0017004 ?sspConcept .
      ?outputConcept skos:prefLabel ?outputConceptLabel .
      BIND("Soil Sequestration Potential" AS ?item)
      BIND(obo:RO_0017004 AS ?propertyOrRole)
      BIND(?outputConceptLabel AS ?valueOrFactorLabel)
    }
  }
}
ORDER BY ?item ?propertyOrRole

PREFIX agrontology: <http://aims.fao.org/aos/agrontology#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX prov: <http://www.w3.org/ns/prov#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX she: <https://soilwise-he.github.io/soil-health#>
PREFIX sio: <http://semanticscience.org/resource/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

SELECT DISTINCT ?pollutantLabel ?sourceLabel ?location ?compoundLabel
WHERE {
  {
    ?pollutant sio:SIO_000128 ?process .
    ?process rdf:object she:Soil .

    OPTIONAL { ?pollutant skos:prefLabel ?prefLabel }
    OPTIONAL { ?pollutant agrontology:hasAbbreviation ?abbrev }
    BIND(COALESCE(?prefLabel, ?abbrev) AS ?pollutantLabel)

    OPTIONAL {
      ?source agrontology:isSourceOf ?sourceProcess .
      ?sourceProcess rdf:object ?pollutant .
      OPTIONAL { ?source skos:prefLabel ?sourcePrefLabel }
      OPTIONAL { ?source agrontology:hasAbbreviation ?sourceAbbrev }
      BIND(COALESCE(?sourcePrefLabel, ?sourceAbbrev) AS ?sourceLabel)
    }

    OPTIONAL {
      ?pollutant she:inputsFrom ?source .
      OPTIONAL { ?source skos:prefLabel ?sourcePrefLabel }
      OPTIONAL { ?source agrontology:hasAbbreviation ?sourceAbbrev }
      BIND(COALESCE(?sourcePrefLabel, ?sourceAbbrev) AS ?sourceLabel)
    }
  }
  UNION
  {
    ?pollutant sio:SIO_000128 she:UrbanSoils .

    OPTIONAL { ?pollutant skos:prefLabel ?prefLabel }
    OPTIONAL { ?pollutant agrontology:hasAbbreviation ?abbrev }
    BIND(COALESCE(?prefLabel, ?abbrev) AS ?pollutantLabel)

    OPTIONAL {
      ?pollutant she:inputsFrom ?source .
      OPTIONAL { ?source skos:prefLabel ?sourcePrefLabel }
      OPTIONAL { ?source agrontology:hasAbbreviation ?sourceAbbrev }
      BIND(COALESCE(?sourcePrefLabel, ?sourceAbbrev) AS ?sourceLabel)
    }
  }
  UNION
  {
    she:OrganicPollutants skos:narrower ?pollutant .

    OPTIONAL { ?pollutant skos:prefLabel ?prefLabel }
    OPTIONAL { ?pollutant agrontology:hasAbbreviation ?abbrev }
    BIND(COALESCE(?prefLabel, ?abbrev) AS ?pollutantLabel)

    OPTIONAL {
      ?pollutant she:inputsFrom ?source .
      OPTIONAL { ?source skos:prefLabel ?sourcePrefLabel }
      OPTIONAL { ?source agrontology:hasAbbreviation ?sourceAbbrev }
      BIND(COALESCE(?sourcePrefLabel, ?sourceAbbrev) AS ?sourceLabel)
    }

    OPTIONAL {
      {
        ?pollutant skos:narrower ?specificCompound .
      }
      UNION
      {
        ?pollutant agrontology:includes ?specificCompound .
      }
      OPTIONAL { ?specificCompound skos:prefLabel ?compoundPrefLabel }
      OPTIONAL { ?specificCompound agrontology:hasAbbreviation ?compoundAbbrev }
      BIND(COALESCE(?compoundPrefLabel, ?compoundAbbrev) AS ?compoundLabel)
    }
  }
  UNION
  {
    ?source agrontology:isSourceOf ?sourceProcess .
    ?sourceProcess obo:BFO_0000066 she:Agriculture .
    ?sourceProcess rdf:object ?pollutant .

    OPTIONAL { ?source skos:prefLabel ?sourcePrefLabel }
    OPTIONAL { ?source agrontology:hasAbbreviation ?sourceAbbrev }
    BIND(COALESCE(?sourcePrefLabel, ?sourceAbbrev) AS ?sourceLabel)

    OPTIONAL { ?pollutant skos:prefLabel ?prefLabel }
    OPTIONAL { ?pollutant agrontology:hasAbbreviation ?abbrev }
    BIND(COALESCE(?prefLabel, ?abbrev) AS ?pollutantLabel)
  }
  FILTER(?pollutant != she:PPPs)
}
ORDER BY ?pollutantLabel ?sourceLabel

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX she: <https://soilwise-he.github.io/soil-health#>
PREFIX agrontology: <http://aims.fao.org/aos/agrontology#>
PREFIX af-x: <http://purl.allotrope.org/ontologies/property#>

SELECT ?subjectLabel ?predicateURI ?objectLabel
WHERE {
  {
    VALUES ?protectionConcept { she:HumanHealthProtection she:EcosystemProtection }
    ?protectionConcept skos:prefLabel ?subjectLabel .
    ?protectionConcept skos:related ?relatedObject .
    VALUES ?relatedObjectType { she:SoilServices she:SoilFunctions }
    ?relatedObject a ?relatedObjectType .
    ?relatedObject skos:prefLabel ?objectLabel .
    BIND(skos:related AS ?predicateURI)
  }
  UNION
  {
    ?keyServiceURI skos:prefLabel ?subjectLabel .
    ?keyServiceURI agrontology:isPartOf she:SoilProtection .
    she:SoilProtection skos:prefLabel ?objectLabel .
    BIND(agrontology:isPartOf AS ?predicateURI)
  }
  UNION
  {
    ?keyServiceURI skos:prefLabel ?subjectLabel .
    ?keyServiceURI agrontology:isPartOf she:SoilProtection .
    ?keyServiceURI skos:related ?prodServiceOutput .
    ?prodServiceOutput skos:prefLabel ?objectLabel .
    BIND(skos:related AS ?predicateURI)
  }
  UNION
  {
    she:SoilProtection skos:prefLabel ?subjectLabel .
    she:SoilProtection af-x:AFX_0000388 ?mainEntity .
    ?mainEntity skos:prefLabel ?objectLabel .
    BIND(af-x:AFX_0000388 AS ?predicateURI)
  }
}
ORDER BY ?subjectLabel ?predicateURI ?objectLabel

PREFIX agrontology: <http://aims.fao.org/aos/agrontology#>
PREFIX biolink: <https://w3id.org/biolink/vocab/>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX qudt: <http://qudt.org/schema/qudt/>
PREFIX she: <https://soilwise-he.github.io/soil-health#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX sorelm: <http://sweetontology.net/relaMath/>
PREFIX sorelsp: <http://sweetontology.net/relaSpace/>
PREFIX unit: <http://qudt.org/vocab/unit/>

SELECT DISTINCT 
    ?groupLabel 
    ?definition
    ?sizeUnit ?sizeValue
    ?diameterUnit ?diameterInterval
    ?lengthUnit ?lengthInterval
    ?biomassUnit ?biomassInterval ?biomassUpperBound ?biomassValue
    ?densityUnit ?densityInterval ?densityUpperBound ?densityValue
    ?freshBiomassUnit ?freshBiomassUpperBound
    ?feedingHabit
    ?consumedItem
    ?narrowerItem
    ?component
WHERE {
    she:FunctionalGroups skos:narrower ?functionalGroup .
    ?functionalGroup skos:prefLabel ?groupLabel .

    OPTIONAL { ?functionalGroup skos:definition ?definition }

    OPTIONAL {
        ?functionalGroup obo:MICRO_0000105 ?sizeBlank .
        ?sizeBlank qudt:numericValue ?sizeValue ;
                   qudt:unit ?sizeUnit .
    }

    OPTIONAL {
        ?functionalGroup sorelsp:diameter ?diameterBlank .
        ?diameterBlank qudt:unit ?diameterUnit ;
                      sorelm:hasInterval ?diameterInterval .
    }

    OPTIONAL {
        ?functionalGroup sorelsp:hasLength ?lengthBlank .
        ?lengthBlank qudt:unit ?lengthUnit ;
                    sorelm:hasInterval ?lengthInterval .
    }

    OPTIONAL {
        ?functionalGroup she:hasBiomass ?biomassBlank .
        ?biomassBlank qudt:unit ?biomassUnit .
        OPTIONAL { ?biomassBlank sorelm:hasInterval ?biomassInterval }
        OPTIONAL { ?biomassBlank sorelm:hasUpperBound ?biomassUpperBound }
        OPTIONAL { ?biomassBlank qudt:numericValue ?biomassValue }
    }

    OPTIONAL {
        ?functionalGroup she:hasDensity ?densityBlank .
        ?densityBlank qudt:unit ?densityUnit .
        OPTIONAL { ?densityBlank sorelm:hasInterval ?densityInterval }
        OPTIONAL { ?densityBlank sorelm:hasUpperBound ?densityUpperBound }
        OPTIONAL { ?densityBlank qudt:valueSN ?densityValue }
    }

    OPTIONAL {
        ?functionalGroup she:hasFreshBiomass ?freshBiomassBlank .
        ?freshBiomassBlank qudt:unit ?freshBiomassUnit ;
                          sorelm:hasUpperBound ?freshBiomassUpperBound .
    }

    OPTIONAL {
        ?functionalGroup skos:broader ?feedingHabit .
        FILTER(?feedingHabit IN (she:Bacterivores, she:Fungivores, she:Herbivores, 
                                she:Omnivores, she:Predators))
    }

    OPTIONAL {
        ?functionalGroup biolink:consumes ?consumedItem .
    }

     OPTIONAL {
        ?functionalGroup skos:narrower ?narrowerItem .
    }

    OPTIONAL {
        ?functionalGroup agrontology:hasComponent ?component .
    }
}
ORDER BY ?groupLabel

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX she: <https://soilwise-he.github.io/soil-health#>
PREFIX dcterms: <http://purl.org/dc/terms/>

SELECT ?indicatorLabel ?relationshipProperty ?relatedItemLabelOrURI
WHERE {
  {
    she:SoilBiodiversityIndicators skos:narrower ?indicatorURI .
    ?indicatorURI skos:prefLabel ?indicatorLabel_val .
    ?indicatorURI dcterms:source ?sourceURI .
    VALUES ?sourceURI { she:Breure2004 she:Huber2008 }
    OPTIONAL { ?sourceURI skos:prefLabel ?sourceLabel_opt . }
    BIND(COALESCE(?sourceLabel_opt, STR(?sourceURI)) AS ?sourceDisplay) .

    BIND(?indicatorLabel_val AS ?indicatorLabel)
    BIND(dcterms:source AS ?relationshipProperty)
    BIND(?sourceDisplay AS ?relatedItemLabelOrURI)
  }
}
ORDER BY ?indicatorLabel ?relationshipProperty ?relatedItemLabelOrURI

PREFIX she: <https://soilwise-he.github.io/soil-health#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX dcterms: <http://purl.org/dc/terms/>
PREFIX schema: <https://schema.org/>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX sorelph: <http://sweetontology.net/relaPhysical/>

SELECT DISTINCT 
    ?mainCategoryLabel
    ?methodLabel
    ?specificIndicatorLabel 
    ?relationshipType ?relatedEntityLabel
WHERE {
    she:BiologicalIndicators dcterms:source she:Ritz2009 ;
                            skos:narrower ?mainCategory .

    ?mainCategory skos:prefLabel ?mainCategoryLabel .

    ?mainCategory skos:narrower ?specificIndicator .
    ?specificIndicator skos:prefLabel ?specificIndicatorLabel .

    OPTIONAL {
        ?mainCategory schema:isBasedOn ?method .
        ?method skos:prefLabel ?methodLabel .
    }

    OPTIONAL {
        {
            ?specificIndicator obo:RO_0002507 ?relatedEntity .
            ?relatedEntity skos:prefLabel ?relatedEntityLabel .
            BIND(obo:RO_0002507 AS ?relationshipType)
        }
        UNION
        {
            ?specificIndicator skos:related ?relatedEntity .
            ?relatedEntity skos:prefLabel ?relatedEntityLabel .
            BIND(skos:related AS ?relationshipType)
        }
        UNION
        {
            ?specificIndicator she:indicates ?relatedEntity .
            ?relatedEntity skos:prefLabel ?relatedEntityLabel .
            BIND(she:indicates AS ?relationshipType)
        }
        UNION
        {
            ?specificIndicator sorelph:from ?relatedEntity .
            ?relatedEntity skos:prefLabel ?relatedEntityLabel .
            BIND(sorelph:from AS ?relationshipType)
        }
    }

    FILTER EXISTS {
        ?mainCategory dcterms:source she:Ritz2009
    }
}
ORDER BY ?mainCategoryLabel ?specificIndicatorLabel

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX she: <https://soilwise-he.github.io/soil-health#>
PREFIX qudt: <http://qudt.org/schema/qudt/>

SELECT ?indicatorCategoryLabel ?indicatorLabel ?valueLabel
WHERE {
  {
    she:TopsoilCompactionParameters skos:prefLabel ?tcpLabel_1 .
    she:TopsoilCompactionParameters skos:narrower ?indicator .
    ?indicator skos:prefLabel ?indicatorLabel_main .

    OPTIONAL { ?indicator qudt:symbol ?symbol_val . }

    BIND(?tcpLabel_1 AS ?indicatorCategoryLabel)
    BIND(?indicatorLabel_main AS ?indicatorLabel)
    BIND(?symbol_val AS ?valueLabel)
  }
}
ORDER BY ?indicatorCategoryLabel ?indicatorLabel ?valueLabel

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX she: <https://soilwise-he.github.io/soil-health#>
PREFIX agrontology: <http://aims.fao.org/aos/agrontology#>

SELECT ?mainConceptLabel ?relationshipURI ?specificTypeLabel
WHERE {
  {
    she:ArtificialSurfaces skos:prefLabel ?mainConceptLabel .
    she:ArtificialSurfaces agrontology:includes ?specificTypeURI .
    ?specificTypeURI skos:prefLabel ?specificTypeLabel .
    BIND(agrontology:includes AS ?relationshipURI)
  }
  UNION
  {
    she:UrbanSoils skos:prefLabel ?mainConceptLabel .
    she:UrbanSoils skos:narrower ?specificTypeURI .
    ?specificTypeURI skos:prefLabel ?specificTypeLabel .
    BIND(skos:narrower AS ?relationshipURI)
  }
}
ORDER BY ?mainConceptLabel ?relationshipURI ?specificTypeLabel
