she:LandDegradationProcesses a skos:Concept ;
    sio:equivalentTo she:SoilThreats ;
    skos:definition "Important land degradation processes are the soil threats." ;
    skos:prefLabel "land degradation processes" .

she:CarbonStocks a skos:Concept ;
    dcterms:source she:SDG ;
    skos:prefLabel "carbon stocks" .

she:ConditionOfLands a skos:Concept ;
    skos:prefLabel "condition of lands" .

she:ConditionOfSoils a skos:Concept ;
    skos:prefLabel "condition of soils" .

she:EcosystemServices a skos:Concept ;
    agrontology:isAffectedBy she:LandDegradation ;
    skos:prefLabel "ecosystem services" .

she:LandCoverChange a skos:Concept ;
    dcterms:source she:SDG ;
    skos:prefLabel "land cover change" .

she:LandDegradation a skos:Concept ;
    skos:prefLabel "land degradation" ;
    she:requires she:LandDegradationIndicators .

she:LandDegradationIndicators a skos:Concept ;
    dcterms:source she:Orr2017 ;
