        she:ConditionOfSoils ;
    skos:definition "Indicators represent 'key processes which underpin land-based natural capital'." ;
    skos:narrower she:CarbonStocks,
        she:LandCoverChange,
        she:LandProductivity ;
    skos:prefLabel "land degradation indicators" ;
    skos:related she:EcosystemServices,
        she:NaturalCapital .

she:LandProductivity a skos:Concept ;
    dcterms:source she:SDG ;
    skos:prefLabel "land productivity" .

she:NaturalCapital a skos:Concept ;
    skos:prefLabel "natural capital" .

she:Orr2017 a dcterms:BibliographicResource ;
    dcterms:date "2017" .

she:SoilThreats a skos:Concept ;
    skos:prefLabel "soil threats" .

she:SDG a sio:SIO_010423 ;
    agrontology:hasAbbreviation "SDG" ;
    skos:prefLabel "sustainable development goal" .

@prefix agrontology: <http://aims.fao.org/aos/agrontology#> .
