she:OrganicResidueCover a skos:Concept ;
    obo:RO_0002212 she:WaterErosion,
        she:WindErosion ;
    skos:prefLabel "organic residue cover" .

she:PlantCover a skos:Concept ;
    obo:RO_0002212 she:WaterErosion,
        she:WindErosion ;
    skos:prefLabel "plant cover" .

she:RegulationEUNo13062013 a sohuj:Regulation ;
    agrontology:hasPart she:GAEC ;
    dcterms:source she:EU2013b ;
    skos:prefLabel "regulation EU No 1306/2013" ;
