@prefix she: <https://soilwise-he.github.io/soil-health#> .
@prefix skos: <http://www.w3.org/2004/02/skos/core#> .
@prefix dcterms: <http://purl.org/dc/terms/> .

she:SoilScreeningValues a skos:Concept ;
    skos:prefLabel "soil screening values" ;
    skos:definition "soil screening values (ssvs) are the levels of pollutants in soil at which the corresponding concentrations in endpoints (e.g. quality standard for food or drinking water) are equal to the critical limits." ;
    she:indicates she:PollutantsInSoil ;
    she:correspondsTo [ 
        she:inEndpoint she:Endpoints ;
        she:equalsCriticalLimit she:CriticalLimits
    ] ;
    she:dependsOn she:FunctionConsidered, she:SoilTypeConsidered ;
    she:hasPathway she:SoilPathway ;
    she:hasScreeningRange she:LowToMediumScreeningLevels, she:HighScreeningLevels ;
    she:isContrastedWith she:RiskLimits ;
    she:isPartOf she:LegislativeFramework .

she:PollutantsInSoil a skos:Concept ;
    skos:prefLabel "pollutants in soil" .

she:Endpoints a skos:Concept ;
    skos:prefLabel "endpoints" ;
    skos:definition "corresponding concentrations in endpoints such as quality standard for food or drinking water." ;
    she:includes she:FoodQualityStandard, she:DrinkingWaterQualityStandard .

she:FoodQualityStandard a skos:Concept ;
    skos:prefLabel "quality standard for food" .

she:DrinkingWaterQualityStandard a skos:Concept ;
    skos:prefLabel "quality standard for drinking water" .

she:CriticalLimits a skos:Concept ;
    skos:prefLabel "critical limits" .

she:FunctionConsidered a skos:Concept ;
    skos:prefLabel "function considered" .

she:SoilTypeConsidered a skos:Concept ;
    skos:prefLabel "soil type considered" .

she:SoilPathway a skos:Concept ;
    skos:prefLabel "pathway between endpoint critical limit and soil concentration" ;
    she:isAffectedBy she:SoilProperties .

she:SoilProperties a skos:Concept ;
    skos:prefLabel "soil properties" ;
    she:includes she:SoilPH .

she:SoilPH a skos:Concept ;
    skos:prefLabel "soil ph" ;
    she:affects she:TransferMetals .

she:TransferMetals a skos:Concept ;
    skos:prefLabel "transfer of metals from soil to crops" .

she:LowToMediumScreeningLevels a skos:Concept ;
    skos:prefLabel "low to medium screening levels" ;
    skos:definition "screening levels ranging from low to medium indicating acceptable risk, no immediate action required." .

she:HighScreeningLevels a skos:Concept ;
    skos:prefLabel "high screening levels" ;
    skos:definition "screening levels beyond which risk is deemed unacceptable and further research or soil remediation would be required." ;
    she:triggersRequirement she:FurtherResearch, she:SoilRemediation .

she:RiskLimits a skos:Concept ;
    skos:prefLabel "risk limits" .

she:LegislativeFramework a skos:Concept ;
    skos:prefLabel "legislative framework" ;
    she:variesAcross she:MemberStates .

