  she:preferredLabel "Undecayed animal residues" .

she:MicrobialBiomass a she:Concept ;
  she:preferredLabel "Microbial biomass" .

she:DecompositionStage a she:Concept ;
  she:preferredLabel "Decomposition stage" .

she:Mineralisation a she:Concept ;
  she:preferredLabel "Mineralisation" .

she:Soil a she:Concept ;
  she:preferredLabel "Soil" .

