she:SOCStock a she:Concept ;
  skos:prefLabel "SOC stock" ;
  skos:altLabel "SOC_stock" ;
  skos:definition "SOC stock represents the pool of organic carbon for a specific layer of soil." ;
  she:symbol "SOC" ;
  she:unit she:TonnesCPerHectare ;
  she:occursIn she:SoilLayer ;
  she:represents she:OrganicCarbonPool ;
  she:dependsOn she:OrganicCarbonContent , she:BulkDensity , she:CoarseMineralFragmentContent , she:LayerThickness ;
  she:isCalculatedBy she:SOCStockEquation .

she:SoilLayer a she:Concept ;
  skos:prefLabel "soil layer" .

she:OrganicCarbonPool a she:Concept ;
  skos:prefLabel "organic carbon pool" ;
  she:isComposedOf she:OrganicCarbon ;
  she:occursIn she:SoilLayer .

she:OrganicCarbon a she:Concept ;
  skos:prefLabel "organic carbon" .

she:OrganicCarbonContent a she:Concept ;
  skos:prefLabel "organic carbon content" ;
  skos:altLabel "SOC content" ;
  she:symbol "C" ;
  she:unit she:GramCPerKilogram ;
  she:isPropertyOf she:SoilLayer .

she:BulkDensity a she:Concept ;
  skos:prefLabel "bulk density" ;
  she:symbol "BD" ;
  she:unit she:KilogramPerSquareMeter ;
  she:isPropertyOf she:SoilLayer .

she:CoarseMineralFragmentContent a she:Concept ;
  skos:prefLabel "coarse mineral fragment content" ;
  skos:altLabel "fraction of coarse material" , "fraction of rock fragments" ;
  she:symbol "CM" ;
  she:note "by mass" ;
  she:isPropertyOf she:SoilLayer ;
  dcterms:source she:PoeplauEtAl2019 .

she:LayerThickness a she:Concept ;
  skos:prefLabel "layer thickness" ;
  skos:altLabel "depth" ;
  she:symbol "d" ;
  she:unit she:Meter ;
  she:isPropertyOf she:SoilLayer .

she:SOCStockEquation a she:Equation ;
  skos:prefLabel "SOC stock calculation" ;
  she:expression "SOC_stock = d * C * (1 - CM) * BD / 100" ;
  she:calculates she:SOCStock ;
  she:hasPart she:LayerThickness , she:OrganicCarbonContent , she:CoarseMineralFragmentContent , she:BulkDensity .

she:TonnesCPerHectare a she:Unit ;
  skos:prefLabel "tonnes C/ha" .

she:GramCPerKilogram a she:Unit ;
  skos:prefLabel "grams C/kg" .

she:KilogramPerSquareMeter a she:Unit ;
  skos:prefLabel "kg/m²" .

she:Meter a she:Unit ;
  skos:prefLabel "m" .

she:PoeplauEtAl2019 a dcterms:BibliographicResource , she:BibliographicResource ;
  dcterms:creator "Poeplau et al." ;
  dcterms:date "2019" .

